RULES_ITEM.SQL_TEXT:
declare 
    v_actual_count number;
    v_integration_run_id integration_run.integration_run_id%type;
    v_integration_read_from_stdout integration.read_from_stdout%type;
    v_integration_log_level integration.log_level%type;
    v_record_numbers number := 3;
    v_integration_name integration.integration_name%type;
begin    
    select log_level, read_from_stdout, integration_name
      into v_integration_log_level, v_integration_read_from_stdout, v_integration_name
      from integration
     where integration_id = :id_num;

    if v_integration_log_level = 3 then
        v_record_numbers := 12;
    elsif v_integration_log_level = 0 then
        v_record_numbers := 9;
    elsif v_integration_log_level = 1 then
        v_record_numbers := 6;
    end if;

    if v_integration_read_from_stdout = 1 then
        v_record_numbers := v_record_numbers + 3;
    end if;

    select count(*)
      into v_actual_count
      from integration_log
     where integration_run_id = :integration_run_id;


    if v_record_numbers <> v_actual_count then
        pkg_test_integration.test_failed(
            p_integration_name => v_integration_name,
            p_error => 'Invalid number of Integration Logs. ' || to_char(v_actual_count) || ' but expected ' ||  to_char(v_record_numbers),
            p_integration_run_id => :integration_run_id);
    else     
        pkg_test_integration.test_succeed(v_integration_name, :integration_run_id);
    end if;

end;
